import Header from "./components/Header";
import Tasks from "./components/Tasks";
import {useState} from 'react';
import AddTask from "./components/AddTask";

function App() {
  const [tasks, setTasks] = useState([
    {   id: 1,      
        text: "Learn React",
        completed: true,
        reminder: false,
        date: "2020-01-01"
    },  
    {   id: 2,
        text: "Learn Redux",
        completed: false,
        reminder: false,
        date: "2020-01-01"
    },
    {   id: 3,
        text: "Learn React Native",

        completed: false,
        reminder: false,
        date: "2020-01-01"


    }
])

const addTask = (text, date,completed,reminder) => {
    setTasks([...tasks, {id: Math.random(), text, date, reminder : false}])
  }


const deleteTask = (id) => {
  setTasks(tasks.filter(task => task.id !== id))
}

const toggleReminder = (id) => {
  setTasks(tasks.map(task => {
    if(task.id === id) {
      return {
        ...task,
        reminder: !task.reminder
      }   
    } else {
    return task;}
  }))
  console.log('toggleReminder')
}

const toggleCompleted = (id) => {
  setTasks(tasks.map(task => {
    if(task.id === id) {
      return {
        ...task,
        completed: !task.completed
      }
    } else {
    return task;}
  }))

  console.log('toggleCompleted')
}



  const name = 'React';
  return (
    <div className="container">
<Header/>
<AddTask/>
{tasks.length > 0 ?<Tasks tasks={tasks} onAdd={addTask} onDelete={deleteTask} onToggle={toggleReminder} onComplete ={toggleCompleted} /> : <p>No tasks</p>}
    </div>
  );
}

export default App;
