import React from 'react'
import { useState } from 'react'

const AddTask = () => {
    const [text, setText] = useState('')
    const [day, setDay] = useState('')
    const [reminder, setReminder] = useState(false)
    const [completed, setCompleted] = useState(false)

  return (
    <form className='add-form'>
      <div className='form-control'>
        <label htmlFor='task'>Add Task</label>
        <input type='text' className='form-control' id='task' placeholder='Add Task' value={text} onChange={(e) => setText(e.target.value)} />
        </div>
        <div className='form-control'>
        <label htmlFor='task'>Date</label>
        <input type='text' className='form-control' id='task' placeholder='Add Task'  value={day} onChange={(e) => setDay(e.target.value)} />
        </div>
        <div className='form-control form-control-check'>
        <label htmlFor='task'>Set Reminder</label>
        <input type='checkbox' className='form-control' id='task' placeholder='Add Checkbox'  value={reminder} onChange={(e) => setReminder(e.target.value)} />
        </div>

        <input type='submit' className='btn btn-block' value='Add Task' />
    </form>
  )
}

export default AddTask
